import React, { Component } from 'react';
import './App.css';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Error from './components/Error';
import Login from './components/Login/Login';
import Profile from './components/Profile/Profile';
import Principale from './components/Principale/Principale';
import Forum from './components/Forum/Forum';
import GetDataForum from './Datas/GetDataForum';

class App extends Component {


  componentDidMount()
  {

        //get UserConexion Data
        const xhr = new XMLHttpRequest();
        xhr.open('GET', 'https://c8f3-154-126-79-169.eu.ngrok.io/get_connected_etudiant/', true);
        xhr.withCredentials = true;
        xhr.onload = () => {
            
            const userData = JSON.parse(xhr.responseText);
            this.setState({ userData });
            
        }
        xhr.send();
  }
  render() {
    
    return (
      <BrowserRouter>
        <Routes>
          <Route path='/'>
            <Route index element={<Principale />} />
            <Route path='login' element={<Login />} />
            <Route path='forum' element={<Forum />} />
            <Route path='profile' element={<Profile />} />
            <Route path='forum/data' element={<GetDataForum />} />
            <Route path='*' element={<Error />} />
          </Route>
        </Routes>
      </BrowserRouter>
    );
  }
}

export default App;
