import React, { Component } from 'react';

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: '',
      password: ''
    };
  }

  handleEmailChange = (e) => {
    this.setState({
      email: e.target.value
    });
  }

  handlePasswordChange = (e) => {
    this.setState({
      password: e.target.value
    });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    console.log(`Email: ${this.state.email}, Password: ${this.state.password}`);
  }

  handleForgotPassword = () => {
    console.log('Forgot Password Clicked');
  }

  render() {
    return (
      <div className="login d-flex justify-content-center align-items-center" style={{ width: '100%', height: '100vh' }}>
        <div className="container" style={{ width: '500px', borderRadius: '50px', boxShadow: '0 0 10px 2px rgba(0, 0, 0, 0.3)', padding: '30px' }}>
          <h2 className="mb-3">Se connecter</h2>
          <form onSubmit={this.handleSubmit}>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email</label>
              <input type="email" className="form-control" id="email" placeholder="Votre email" value={this.state.email} onChange={this.handleEmailChange} />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Mot de passe</label>
              <input type="password" className="form-control" id="password" placeholder="Votre mot de passe" value={this.state.password} onChange={this.handlePasswordChange} />
            </div>
            <button type="submit" className="btn btn-primary">Se connecter</button>
          </form>
          <div className="d-flex justify-content-between mt-3">
            <button className="btn btn-danger">Se connecter avec Facebook</button>
            <button className="btn btn-secondary">Se connecter avec Gmail</button>
          </div>
          <div className="mt-3 text-center">
            <button className="btn btn-link" onClick={this.handleForgotPassword}>J'ai oubli√© mon mot de passe</button>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
