import React, { Component } from 'react';
import './App.css';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Error from './components/Error';
import Login from './components/Login/Login';
import Profile from './components/Profile/Profile';
import Principale from './components/Principale/Principale';
import Forum from './components/Forum/Forum';
import GetDataForum from './Datas/GetDataForum';

class App extends Component {


  componentDidMount()
  {

        //get UserConnexion Data
        const xhr = new XMLHttpRequest();
        xhr.open('GET', 'https://c8f3-154-126-79-169.eu.ngrok.io/get_connected_etudiant/', true);
        xhr.withCredentials = true;
        xhr.onload = () => {
            
            const userData = JSON.parse(xhr.responseText);
            this.setState({ userData });
            
        }
        xhr.send();
  }
  render() {
    const { userData } = this.state;
        if (!userData) {
            return <div>Loading...</div>; // Ou un autre élément d'affichage pendant le chargement
        }
        if(userData === {})
        {
          return (
            <BrowserRouter>
              <Routes>
                <Route path='/'>
                  <Route index element={<Login />} />
                  <Route path='login' element={<Login />} />
                  <Route path='*' element={<Error />} />
                </Route>
              </Routes>
            </BrowserRouter>
          );
        }
    
  }
}

export default App;
