    {
        super(props)

        this.state = {
            userDatas: null
        };
    }

    componentDidMount()
    {

        //get Forum Data
        const xhr = new XMLHttpRequest();
        xhr.open('GET', 'https://c8f3-154-126-79-169.eu.ngrok.io/forum', true);
        xhr.withCredentials = true;
        xhr.onload = () => {
            
            const userDatas = JSON.parse(xhr.responseText);
            this.setState({ userDatas });
            
        }
        xhr.send();
    }

    render()
    {
